/* -- newWindow --*/
function newWindow() {
$IPBHTML = "";
$IPBHTML .= "<script type='text/javascript'>
// And then move
document.observe(\"dom:loaded\", function(){
	$('nav_app_ipchat').down('a').observe( 'click', function(e) {
		var _chatUrl	= $('nav_app_ipchat').down('a').href;
	
		if( _chatUrl.charAt( _chatUrl.length - 1 ) == '#' )
		{
			_chatUrl	= _chatUrl.substr( 0, _chatUrl.length - 1 );
		}
	
		if( _chatUrl.indexOf('?') != '-1' )
		{
			_chatUrl	+= '&_popup=1';
		}
		else
		{
			_chatUrl	+= '?_popup=1';
		}
		
		window.open( _chatUrl, \"chatpopout\", \"status=0,toolbar=0,location=1,menubar=0,directories=0,resizable=1,scrollbars=1,height=550,width=750\" );
		
		Event.stop(e);
		return false;
	});
});
</script>";
return $IPBHTML;
}

/* -- tabCount --*/
function tabCount($count) {
$IPBHTML = "";
$IPBHTML .= "" . (((!$this->settings['ipchat_htc_view'] OR IPS_APP_COMPONENT != 'ipchat') AND (!$this->settings['ipchat_htc_zero'] OR $count > 0)) ? ("
<div id='chat-tab-count-wrap'><span id='chat-tab-count' class='ipsHasNotifications' title='" . sprintf( $this->lang->words['chattab_count'], $count ) . "' style='display:none;'>{$count}</span></div>
<script type='text/javascript'>
// And then move
document.observe(\"dom:loaded\", function(){
	var _thisHtml	= $('nav_app_ipchat').down('a').innerHTML;
	_thisHtml = _thisHtml + $('chat-tab-count-wrap').innerHTML;
	$('nav_app_ipchat').down('a').update( _thisHtml ).setStyle( { position: 'relative' } );
	$('chat-tab-count-wrap').remove();
	$('chat-tab-count').show();
});
</script>
") : ("")) . "";
return $IPBHTML;
}

